const N = 200

var n, k int
var a []int
var adj [N][]int
var g, b, spf [N]int

func dfs1(v int, par int) {
	g[v] = a[v]
	for _, ch := range adj[v] {
		if ch == par {
			continue
		}
		dfs1(ch, v)
		gcd := func(a, b int) int {
			for a != 0 {
				a, b = b%a, a
			}
			return b
		}
		g[v] = gcd(g[v], g[ch])
	}
}

func dfs2(v int, par int, target int) int {
	if g[v]%target == 0 {
		return 0
	} else if a[v]%target != 0 {
		return n + 1
	}
	c := 0
	for _, ch := range adj[v] {
		if ch == par || g[ch]%target == 0 {
			continue
		}
		c += 1 + dfs2(ch, v, b[target])
	}
	return c
}
