func (t *treap) rank(key $VAR0$) (kth int) {
	for o := t.root; o != nil; {
		switch c := o.cmp(key); {
		case c == 0:
			o = o.lr[0]
		case c > 0:
			kth += o.lr[0].size() + 1 // 1 <-> o.dupCnt
			o = o.lr[1]
		default:
			kth += o.lr[0].size() 
			// 额外加上 1 或 o.dupCnt 就是 <= key 的元素个数
			return
		}
	}
	return
}

