// 有 k 个元素小于 o.key
func (t *treap) kth(k int) (o *node) {
	//if k < 0 || k >= t.root.size() {
	//	return
	//}
	for o = t.root; o != nil; {
		if ls := o.lr[0].size(); k < ls {
			o = o.lr[0]
		} else {
			k -= ls + 1 // 1 <-> o.dupCnt
			if k < 0 {
				return
			}
			o = o.lr[1]
		}
	}
	return // NOTE: check nil
}

