combinations := func(n, r int, do func(ids []int) (Break bool)) {
	ids := make([]int, r)
	for i := range ids {
		ids[i] = i
	}
	if do(ids) {
		return
	}
	for {
		i := r - 1
		for ; i >= 0; i-- {
			if ids[i] != i+n-r {
				break
			}
		}
		if i == -1 {
			return
		}
		ids[i]++
		for j := i + 1; j < r; j++ {
			ids[j] = ids[j-1] + 1
		}
		if do(ids) {
			return
		}
	}
}

permutations := func(n, r int, do func(ids []int) (Break bool)) {
	ids := make([]int, n)
	for i := range ids {
		ids[i] = i
	}
	if do(ids[:r]) {
		return
	}
	cycles := make([]int, r)
	for i := range cycles {
		cycles[i] = n - i
	}
	for {
		i := r - 1
		for ; i >= 0; i-- {
			cycles[i]--
			if cycles[i] == 0 {
				tmp := ids[i]
				copy(ids[i:], ids[i+1:])
				ids[n-1] = tmp
				cycles[i] = n - i
			} else {
				j := cycles[i]
				ids[i], ids[n-j] = ids[n-j], ids[i]
				if do(ids[:r]) {
					return
				}
				break
			}
		}
		if i == -1 {
			return
		}
	}
}

type trieNode struct {
	son [$SON_SIZE$]*trieNode
	val int
	cnt int
}

type trie struct{ root *trieNode }

func (trie) ord(c byte) byte { return c - '$BASE_CHAR$' }

func (t *trie) put(s []byte, val int) *trieNode {
	o := t.root
	for _, b := range s {
		b = t.ord(b)
		if o.son[b] == nil {
			o.son[b] = &trieNode{}
		}
		o = o.son[b]
		//o.cnt++ // 统计子树大小的写法
		//o.val = val // 更新 s 的所有前缀的值
	}
	o.cnt++
	o.val = val
	return o
}

func (t *trie) find(s []byte) *trieNode {
	o := t.root
	for _, b := range s {
		o = o.son[t.ord(b)]
		if o == nil {
			return nil
		}
	}
	if o.cnt == 0 {
		return nil
	}
	return o
}

func newTrie() *trie {
	return &trie{&trieNode{}}
}

