type nb struct{ to, wt int }

func dijkstra(g [][]nb, st int) []$TYPE$ {
	const inf $TYPE$ = 1e18 // 1e9+1
	dis := make([]$TYPE$, len(g))
	for i := range dis {
		dis[i] = inf
	}
	dis[st] = 0
	h := hp{{st, 0}}
	for len(h) > 0 {
		vd := h.pop()
		v := vd.v
		if dis[v] < vd.dis {
			continue
		}
		for _, e := range g[v] {
			w, wt := e.to, $TYPE$(e.wt)
			if newD := dis[v] + wt; newD < dis[w] {
				dis[w] = newD
				h.push(vdPair{w, dis[w]})
			}
		}
	}
	return dis
}

