func (t seg) maintain(o int) {
	t[o].min = min(t[o<<1].min, t[o<<1|1].min)
}

// t := make(seg, n*4)
// t.build(a, 1, 1, n)$END$
func (t seg) build(a []int, o, l, r int) {
	t[o].l, t[o].r = l, r // 注意 todo 初值
	if l == r {
		t[o].min = $TYPE$(a[l-1])
		return
	}
	m := (l + r) >> 1
	t.build(a, o<<1, l, m)
	t.build(a, o<<1|1, m+1, r)
	t.maintain(o)
}

func (t seg) do(O int, v $TYPE$) {
	o := &t[O]
	o.min += v
	o.todo += v
}

func (t seg) spread(o int) {
	if v := t[o].todo; v != 0 { // 注意 todo 初值
		t.do(o<<1, v)
		t.do(o<<1|1, v)
		t[o].todo = 0
	}
}

func (t seg) update(o, l, r, v int) {
	if l <= t[o].l && t[o].r <= r {
		t.do(o, $TYPE$(v))
		return
	}
	t.spread(o)
	m := (t[o].l + t[o].r) >> 1
	if l <= m {
		t.update(o<<1, l, r, v)
	}
	if m < r {
		t.update(o<<1|1, l, r, v)
	}
	t.maintain(o)
}

func (t seg) query(o, l, r int) $TYPE$ {
	if l <= t[o].l && t[o].r <= r {
		return t[o].min
	}
	t.spread(o)
	m := (t[o].l + t[o].r) >> 1
	if r <= m {
		return t.query(o<<1, l, r)
	}
	if l > m {
		return t.query(o<<1|1, l, r)
	}
	return min(t.query(o<<1, l, r), t.query(o<<1|1, l, r))
}

