func modifyNodes(head *ListNode, f func(a []int) (res []int)) *ListNode {
	a := []int{}
	for o := head; o != nil; o = o.Next {
		a = append(a, o.Val)
	}

	res := f(a)

	if len(res) == 0 {
		return nil
	}

	head = &ListNode{Val: res[0]}
	cur := head
	for _, v := range res[1:] {
		cur.Next = &ListNode{Val: v}
		cur = cur.Next
	}
	return head
}

