func pow(x int$TYPE$, n int) (res int$TYPE$) {
    x %= mod
	res = 1
	for ; n > 0; n >>= 1 {
		if n&1 > 0 {
			res = res * x % mod
		}
		x = x * x % mod
	}
	return
}
